$(document).ready(function(){$(document).keyup(function(t){80===t.keyCode&&$("body").toggleClass("philo-no"),67===t.keyCode&&$("body").toggleClass("cookie-yes")}),$(".page-down").click(function(){var t=$(this).parent().next().offset().top,a=750;$("body, html").animate({scrollTop:t},a)}),$(window).on("scroll resize",function(){if(!Modernizr.touch){var t=$(window).scrollTop();$("[data-parallax]").each(function(){var a=$(this).attr("data-offset"),s=$(this).attr("data-speed"),o=-(a-t)*s;"x"===$(this).attr("data-axis")?$(this).css("transform","translateX("+o+"px)"):$(this).css("transform","translateY("+o+"px)")})}}),$("[data-parallax]").each(function(){if(!Modernizr.touch){var t=$(this).offset().top-.5*$(window).height(),a=$(this).attr("data-speed"),s=-t*a;$(this).attr("data-offset",t),"x"===$(this).attr("data-axis")?$(this).css("transform","translateX("+s+"px)"):$(this).css("transform","translateY("+s+"px)")}})});