$(document).ready(function(){$(document).keyup(function(o){80===o.keyCode&&$("body").toggleClass("philo-no"),67===o.keyCode&&$("body").toggleClass("cookie-yes")}),$(document).scroll(function(){$(this).scrollTop()>0?$("header").addClass("is-scrolled"):$("header").removeClass("is-scrolled")}),$("a[href=#bring-philo-to-you]").click(function(){$(document.body).animate({scrollTop:$("#bring-philo-to-you").offset().top},1e3)}),$(".dropdown > a").click(function(o){$(this).addClass("active"),o.stopPropagation()}),$(document).click(function(){$(".dropdown > a.active").removeClass("active")})});