$(document).ready(function(){$("[data-parallax]").each(function(){var t=$(this).offset().top-.5*$(window).height(),a=$(this).attr("data-speed"),s=-t*a;$(this).attr("data-offset",t),"x"===$(this).attr("data-axis")?$(this).css("transform","translateX("+s+"px)"):$(this).css("transform","translateY("+s+"px)")}),$(window).on("scroll resize",function(){var t=$(window).scrollTop();$("[data-parallax]").each(function(){var a=$(this).attr("data-offset"),s=$(this).attr("data-speed"),r=-(a-t)*s;"x"===$(this).attr("data-axis")?$(this).css("transform","translateX("+r+"px)"):$(this).css("transform","translateY("+r+"px)")}),$("[data-background-parallax").each(function(){var a=$(this).attr("data-speed"),s=t*a,r=s+"px";$(this).css("background-position-y",r)})})});